'use strict';

/**
 * The core plugin for the 2D map engine. Handles moving the map by dragging the map with mouse or touch event.
 * Core plugins can always be overwrote if needed.
 *
 * @require Mobile part requires Hammer.js
 * */

/***********************
******** IMPORT ********
***********************/
import { eventListeners as eventListenerMod, utils } from '/components/bundles/coreBundle';

/***********************
********* API **********
***********************/
export const pluginName = "map_drag";
export const map_drag = setupMap_drag();

/***********************
******** PUBLIC ********
***********************/
function setupMap_drag() {
  /* Function for setting and getting the mouse offset. Private functions declared bottom */
  var offsetCoords = _offsetCoords();
  var eventListenerCB, eventListener;

  /***********************
  ********* API **********
  ***********************/
  return {
    init,
    _startDragListener /* Function revealed for testing */
  };

  /**************************
  ********* PUBLIC **********
  **************************/
  /**
   * Required init functions for the plugin
   *
   * @param {Map object} mapObj - the Map class object
   * */
  function init(map) {
    eventListener = eventListenerMod();
    eventListenerCB = _startDragListener(map);

    /* Singleton should have been instantiated before, we only retrieve it with 0 params */
    eventListener.toggleDragListener(eventListenerCB);
  }

  /*****************************
  ********** PRIVATE ***********
  *****************************/
  /**
   * Mobile version. Starts the functionality, uses Hammer.js heavily for doing the drag. More simple and better than
   * desktop version, since we don't need to calculate the drag with several event listener, one is enough with Hammer
   *
   * @private
   * @param {Map Object} map        The current map object
   */
  function _startDragListener( map ) {
    var initialized = false;

    return function startDrag(e) {
      var coords;

      if (eventListener.getState("zoom")) {
        return false;
      }
      coords = utils.mouse.eventData.getHAMMERPointerCoords(e);

      map.mapMoved(true);

      coords.x = Math.round( coords.x );
      coords.y = Math.round( coords.y );

      if (!initialized) {
        offsetCoords.setOffset({
          x: coords.x,
          y: coords.y
        });
        initialized = true;

        return;
      } else if (e.isFinal === true) {
        initialized = false;
        map.mapMoved(false, true);
      }

      _mapMovement(e, map, coords);
    };
  }

  /**
   * This handles offset Changes and setting data has map been moved based on it. Also
   * sets basic settings like preventDefault etc.
   *
   * @private
   * @param  {Event} e                        The event being dealt with
   * @param  {Map Object} map                 The map object
   * @param  {x: Number, y: Number} coords    Current pointer coordinates
   */
  function _mapMovement(e, map, coords) {
    var offset, moved;

    offset = offsetCoords.getOffset();
    moved = {
      x: coords.x - offset.x,
      y: coords.y - offset.y
    };

    if (moved.x > 0 || moved.y > 0 || moved.x < 0 || moved.y < 0) {
      map.moveMap(moved);
    } else {
      map.mapMoved(false);
    }

    offsetCoords.setOffset({
      x: coords.x,
      y: coords.y
    });

    e.preventDefault();
  }
  /**
   * Function for setting and getting the mouse offset.
   * Offset is the distance from the left upper coordinates (global 0, 0 coordinates) on the canvas, to the current /
   * last known mouse coordinates
   */
  function _offsetCoords() {
    var offsetCoords;

    /****** API ******/
    return {
      setOffset,
      getOffset
    };

    function setOffset(coords) {
      return ( offsetCoords = coords );
    }
    function getOffset() {
      return offsetCoords;
    }
  }
}

'use strict';

/**
 * This handles the selction of areas and objects in the map. Currently is uses subcontainers or quadtree to do the selection.
 *
 * @todo It might be a good idea to make the hitDetection more extensive. Now it just uses point or rectangle / bounds to detect hits. It could use sprites or forms. Since we do most work with quadtree, resources shouldn't be the issue.
 */

/***********************
******** IMPORT ********
***********************/
import { Quadtree } from './utils/Quadtree';
import { arrays } from '/components/utilities/general';

/***********************
********* API **********
***********************/
/**
 * this module is responsible for doing hitTesting, like returning the units on certain clicked coordinates or
 * when objects or areas collide with each other.
 * It uses quadtree for preliminary filtering of matching objects and then the framework specific collision detection
 */
export class ObjectManager {
  /**
   * @param {object} hitDetector Object or function that handles hit detection. This can be omitted in many cases
   */
  constructor(hitDetector) {
    this.quadtrees = {};
    this.hitDetector = hitDetector || {};
  }
  /**
   * Retrieve objects under certain coordinates or area, if size is given
   *
   * @param {{ globalCoords: { x:Number, y:Number }, localCoords: { x:Number, y:Number } }} allCoords         the coordinates which we want to hitTest
   * @param {string} type                                         type of the object / layer that we want to retrieve
   * @param {Object} options                                      optional options
   * @param {Object[]} options.subcontainers                      The subcontainers we match against
   * @param {{ width: Integer, height: Integer }} options.size    Size of the rectangle area to match against, if we want to match rectangle instead of one point
   * @return Array of matched objects
   */
  retrieve(allCoords, type, options = { quadtree: false, subcontainers: [], size: { width: 0, height: 0 } }) {
    var { subcontainers, size } = options;
    var { globalCoords, localCoords } = allCoords;
    var foundObjs = [];
    var quadtreeObjs;

    if (subcontainers.length === 0) {
      if (!type) {
        quadtreeObjs = Object.keys(this.quadtrees).map((thisIndex) => {
          return this.quadtrees[thisIndex].retrieve(localCoords, size);
        });
      } else {
        quadtreeObjs = this.quadtrees[type].retrieve(localCoords, size);
      }

      foundObjs = arrays.flatten2Levels(quadtreeObjs);

      if (!size.width || !size.height) {
        foundObjs = foundObjs.filter(obj => {
          let isHit = obj.hitTest ? obj.hitTest(globalCoords, { hitDetector: this.hitDetector }) : true;

          return isHit;
        });
      }
    } else {
      subcontainers.forEach(container => {
        foundObjs = foundObjs.concat(container.children);
      });

      if (!size.width || !size.height) {
        foundObjs = foundObjs.filter(obj => {
          let isHit = obj.hitTest ? obj.hitTest(globalCoords, { hitDetector: this.hitDetector }) : true;

          return isHit;
        });
      }
    }

    return foundObjs;
  }
  /**
   * Add object to hitDetection layer
   *
   * @param {string} type                                                 type of the object / layer that we want to add
   * @param {x:Number, y:Number, width:Number, height:Number} hitArea     area that is hitTested
   * @param {Object} obj                                                  object that we want to store. If hitTest succeeds this object is returned.
   */
  addObject(type, hitArea, obj) {
    if (!this.quadtrees[type]) {
      throw new Error("Could not add object to objectManager layer, layer not found! (" + type + ")");
    }

    return this.quadtrees[type].add({
        x: hitArea.x,
        y: hitArea.y
      }, {
        width: hitArea.width,
        height: hitArea.height
      },
      obj
    );
  }
  /**
   * Add hitDetection layer. Basically you create layers for different object types, like for example one for the
   * terrain, one for the units and one for the cities. The quadtree does not really require bounds, so we omit them.
   *
   * @param {string} type                                   Type of the layer that we want to add
   * @param {{width: Integer, height: Integer}} bounds      Bounds for the quadtree layer
   * @param {{objects: Number, levels: Number}} extra       quadtree-settings: maximum objects before we split and maximum levels of nested layers
   */
  addLayer(type, bounds, extra = {}) {
    this.quadtrees[type] = new Quadtree(bounds, {
        objects: extra.objects || 10,
        levels: extra.levels || 5
      });

    return this.quadtrees[type];
  }
}

'use strict';

/**
 * The actual objects used on the map (such as terrain and units), under containers. All of the objects need to have same argumentAPI for creating objects: coords, data, options
 */

/************************
********* IMPORT ********
*************************/
import { utils } from '/components/bundles/coreBundle';

/************************
********** API **********
*************************/
export class Object_sprite extends PIXI.Sprite {
  /**
   * The base class of all sprite objects
   *
   * @extends PIXI.Sprite
   * @param {PIXI.Point} coords                         the coordinate where the object is located at, relative to it's parent
   * @param {Object} data                               objects data, that will be used in the game. It will not actually be mainly used in graphical but rather things  like unit-data and city-data presentations etc.
   * @param {Object} options.currFrame       currFrame the current frames number. This is basically the initial image, we can change it later for animation or such
   */
  constructor(coord = { x: 0, y: 0 }, data = {}, options = { currentFrame: {} }) {
    var { currentFrame } = options;

    super(currentFrame);

    /* We need to round the numbers. If there are decimal values, the graphics will get blurry */
    let exactCoords = {
      x: Math.round(coord.x),
      y: Math.round(coord.y)
    };
    this.position.set(exactCoords.x,  exactCoords.y);
    /**
     * Name of the object. Used mostly for debugging
     *
     * @attribute
     * @type {String}
     */
    this.name = "Objects_sprite_" + this.id;
    /**
     * Type of the object. Can be used for filtering, ordering or finding correct objects.
     *
     * @attribute
     * @type {String}
     */
    this.type = "None";
    /**
     * Is the object highligtable.
     *
     * @attribute
     * @type {Boolean}
     */
    this.highlightable = true;
    /**
     * Is the object selectable.
     *
     * @attribute
     * @type {Boolean}
     */
    this.selectable = true;
    /**
     * Objects custom data. Holds unit statistics and most data. Like unit movement speed etc.
     *
     * @attribute
     * @type {Object}
     */
    this.data = data;
    /**
     * Current frame (from spritesheet) we are showing.
     *
     * @attribute
     * @type {Number}
     */
    this.currentFrame = currentFrame;
    /**
     * Object area width in pixels.
     *
     * @attribute
     * @type {Number}
     */
    this.areaWidth = this.width;
    /**
     * Object area height in pixels.
     *
     * @attribute
     * @type {Number}
     */
    this.areaHeight = this.height;
  }
  /**
   * Drawing the object
   *
   * @param {Number} x coordinate x
   * @param {Number} y coordinate y
   * @return this object instance
   */
  innerDraw(x, y) {
    this.fromFrame ( this.currentFrame );
    this.x = x;
    this.y = y;

    return this;
  }
  /**
   * Draws new frame to animate or such
   *
   * @param {Number} x                coordinate x
   * @param {Number} y                coordinate y
   * @param {Number} newFrame         New frame number to animate to
   * @return this object instance
   */
  drawNewFrame(x, y, newFrame) {
    this.currentFrame = newFrame;

    return this.innerDraw(x, y);
  }
  /**
   * Get the area that is reserved for the graphical presenation of this object as a rectangle.
   *
   * @param  {Object} options       toGlobal: Boolean. Should the method return global coordinates or local (movableLayer)
   * @return {Object}               { x: Number, y: Number, width: Number, height: Number}
   */
  getGraphicalArea(options = { toGlobal: true } ) {
    var coordinates;

    coordinates = options.toGlobal ? this.toGlobal(new PIXI.Point(0,0)) : this;

    return {
      x: Math.round( coordinates.x ),
      y: Math.round( coordinates.y ),
      width: Math.round( this.width ),
      height: Math.round( this.height )
    };
  }
  /**
   * Coordinate conversion: localToLocal
   *
   * @param  {Number} x                     X coordinate
   * @param  {Number} y                     Y coordinate
   * @param  {PIXI.DisplayObject} target    The DisplayObject where we should target the coordinates for
   * @return {PIXI.Point}                   Coordinates
   */
  localToLocal(x, y, target) {
    var globalCoords = this.toGlobal( { x, y } );
    var targetLocalCoords = target.toLocal( globalCoords );

    return targetLocalCoords;
  }
  /**
   * Clone object
   *
