function setupMouseUtils() {
  return {
    isRightClick,
    eventData: {
      getPointerCoords,
      getHAMMERPointerCoords
    },
    eventMouseCoords
  };

  /**
   * Detects if the mouse click has been the right mouse button
   *
   * @param {Event} event The event where the click occured
   */
  function isRightClick( event ) {
    var rightclick;

    event = event ? event : window.event; /* For IE. */
    if ( event.buttons ) {
      rightclick = ( +event.buttons === 2 );
    } else if ( event.which ) {
      rightclick = ( +event.which === 3 );
    } else if ( event.button ) {
      rightclick = ( +event.button === 2 );
    }

    if ( rightclick ) {
      return true;
    }

    return false;
  }
  function getPointerCoords(e) {
    return {
      x: e.offsetX,
      y: e.offsetY
    };
  }
  function getHAMMERPointerCoords(e) {
    return e.center;
  }
  function eventMouseCoords(e) {
    var pos = {
      x:0,
      y:0
    };

    if (!e) {
      e = window.event;
    }
    if (e.pageX || e.pageY)   {
      pos.x = e.pageX;
      pos.y = e.pageY;
    } else if (e.clientX || e.clientY)  {
      pos.x = e.clientX + document.body.scrollLeft + document.documentElement.scrollLeft;
      pos.y = e.clientY + document.body.scrollTop + document.documentElement.scrollTop;
    }
    // posx and posy contain the mouse position relative to the document
    // Do something with this information
    return pos;
  }
}
function setupResizeUtils() {
  return {
    toggleFullScreen,
    setToFullSize,
    getWindowSize
